let notables = { notables: [] };

dom.window.document.querySelectorAll("tr").forEach((node, key) => {
  let name = null;
  let imgSrc = null;

  if (node.querySelectorAll("a").length >= 3) {
    name = node.querySelector("a").innerHTML;
  } else if (node.querySelector("span.new")) {
    name = node.querySelector("span.new").innerHTML;
  }

  if (node.querySelector("img")) {
    imgSrc = node.querySelector("img").getAttribute("data-src");
    if (!imgSrc) {
      imgSrc = node.querySelector("img").src;
    }
  }

  if (name && imgSrc) {
    notables.notables.push({ name, imgSrc, isKeystone: false });
  }
});

keystoneDom.window.document.querySelectorAll("tr").forEach((node, key) => {
  let name = null;
  let imgSrc = null;

  if (node.querySelectorAll("a").length >= 3) {
    name = node.querySelector("a").innerHTML;
  } else if (node.querySelector("span.new")) {
    name = node.querySelector("span.new").innerHTML;
  }

  if (node.querySelector("img")) {
    imgSrc = node.querySelector("img").getAttribute("data-src");
    if (!imgSrc) {
      imgSrc = node.querySelector("img").src;
    }
  }

  if (name && imgSrc) {
    notables.notables.push({ name, imgSrc, isKeystone: true });
  }
});

const fs = require("fs");

fs.writeFile("./notables.json", JSON.stringify(notables), (err) => {
  if (err) {
    console.error(err);
    return;
  }

  console.log("file saved !");
});
